    private void calculateButtonActionPerformed(java.awt.event.ActionEvent evt) {                                                
        // TODO add your handling code here:
        input = Float.parseFloat(userInputTextField.getText());
        
        TaxCalculator.main(null);
        
        personalAllowanceTextField.setText("");
        if(calculationType == "annually")
        {
            personalAllowanceTextField.setText(String.valueOf(PersonalAllowance.getAnnualPersonalAllowance()));
        }
        else if(calculationType == "monthly")
        {
            personalAllowanceTextField.setText(String.valueOf(PersonalAllowance.getMonthlyPersonalAllowance()));
        }
        
        taxableIncomeTextField.setText("");
        if(calculationType =="annually")
        {
            taxableIncomeTextField.setText(TaxCalculator.getAnnualTaxableIncome());
        }
        else if(calculationType == "monthly")
        {
            taxableIncomeTextField.setText(TaxCalculator.getMonthlyTaxableIncome());
        }
        
        totalTaxTextField.setText("");
        totalTaxTextField.setText(String.valueOf(IncomeTax.getIncomeTax()));
        
        niContributionTextField.setText("");
        niContributionTextField.setText(String.valueOf(NIContribution.getContribution()));
        
        netSalaryTextField.setText("");
        netSalaryTextField.setText(String.valueOf(input - (IncomeTax.getIncomeTax() + NIContribution.getContribution())));
        
        descriptionTextArea.setText(IncomeTax.getText());
    }                                               

    private void monthlyRadioButtonActionPerformed(java.awt.event.ActionEvent evt) {                                                   
        // TODO add your handling code here:
        if(monthlyRadioButton.isSelected())
        {
            calculationType = "monthly";
            annuallyRadioButton.setSelected(false);
        }
        
    }                                                  

    private void annuallyRadioButtonActionPerformed(java.awt.event.ActionEvent evt) {                                                    
        // TODO add your handling code here:
        if(annuallyRadioButton.isSelected())
        {
            calculationType = "annually";
            monthlyRadioButton.setSelected(false);
        }
    }                                                   

    
